mapper.insert:
INSERT INTO HOSPITAL(hospital_id
								, hospital_pw
								, hospital_name
								, hospital_address
								, hospital_phone
								, hospital_email
								, hospital_open
								, hospital_close
								, hospital_species
								, hospital_intro)
			VALUES(#{hospitalId}
								, #{hospitalPw}
								, #{hospitalName}
								, #{hospitalAddress}
								, #{hospitalPhone}
								, #{hospitalEmail}
								, #{hospitalOpen}
								, #{hospitalClose}
								, 'HOSPITAL'
								, #{hospitalIntro, jdbcType=VARCHAR})

mapper.select:
SELECT hospital_id
FROM HOSPITAL
WHERE hospital_id = #{hospitalId}

SELECT hospital_email
FROM HOSPITAL
WHERE hospital_email = #{hospitalEmail}

SELECT *
FROM HOSPITAL
WHERE hospital_id = #{hospitalId}
AND hospital_pw = #{hospitalPw}

mapper.update:
UPDATE HOSPITAL 
SET	hospital_pw = #{hospitalPw}
	, hospital_address = #{hospitalAddress}
	, hospital_phone = #{hospitalPhone}
	, hospital_email = #{hospitalEmail}
	, hospital_intro = #{hospitalIntro}
WHERE hospital_id = #{hospitalId}
