mapper.insert:

	SELECT IFNULL(MAX(user_no),0)+1 AS user_no FROM USER

INSERT INTO USER( user_no
								, user_id
								, password
								, user_name
								, address
								, tel
								, user_email
								, last_date
								, role
								)
			VALUES ( #{userNo}
								, #{userId}
								, #{password}
								, #{userName}
								, #{address}
								, #{tel}
								, #{userEmail}
								, NOW()
								, 'USER'
								)
